mapper.select:
select
,d.drug_name, e.employee_name, o.create_time from tb_output o, tb_employee e, tb_drug d where o.is_deleted = 0 and e.id = o.employee_id and d.drug_id = o.drug_id order by output_id desc
limit #{start},#{limit}

select
,d.drug_name, e.employee_name, o.create_time from tb_output o, tb_employee e, tb_drug d where o.is_deleted = 0 and e.id = o.employee_id and o.employee_id=#{employeeId} and d.drug_id = o.drug_id order by output_id desc
limit #{start},#{limit}
